<?phpinclude "header.php";include "mysqli_connection.php";if (!isset($_SESSION["username"])){        echo "<script>window.location.href='index.php'</script>";}else{//How to get af_id from af page? This page should have a default af_id. Want to show the test cases for 1 particular af_id.?><div><h1>Test Cases</h1></div><div><form action = "#" method = "GET"><input type ="hidden" name="id" value="<?php echo $_GET['id'];?>" /><label for = "title">Title: </label><input type = "text" name = "title" value = "<?php echo $_GET['title'];?>" /><label for = "description">Description: </label><input type = "text" name = "description" value = "<?php echo $_GET['description'];?>" /><input type = "submit" value = "Search" /><input type = "button" value = "Clear" onclick = "window.location.href = 'testcases.php?id=<?php echo $_GET['id'];?>';" /></form></div><div><a href="addtc.php?af_id=<?php echo $_GET['id'];?>">Add new Test Case</a></div><hr /><div><table><thead><td>Test Case ID</td><td>Title</td><td>Description</td><td>Operation</td></thead><tbody><?php$filter = "testcases.af_id = " . $_GET["id"];if (isset($_GET["t_id"]) && $_GET["t_id"] !== ""){        $param = "(t_id = " . $mysqli->real_escape_string(trim($_GET["t_id"])) . ")";        $filter .= " AND " . $param;}if (isset($_GET["title"]) && $_GET["title"] !== ""){        $param = "(title LIKE '%" . $mysqli->real_escape_string(trim($_GET["title"])) . "%')";        $filter .= " AND " . $param;}if (isset($_GET["description"]) && $_GET["description"] !== ""){		$param = "(description LIKE '%" . $mysqli->real_escape_string(trim($_GET["description"])) . "%')";        $filter .= " AND " . $param;}$perNumber = 25;$page = $_GET['page'];$count = $mysqli->query("select count(*) from testcases WHERE " . $filter . ";");if ($count){	$rs = mysqli_fetch_array($count);	$totalNumber = $rs[0];}else	$totalNumber = 0;if ($totalNumber == 0){        echo "<div><font color='red'>There is no results found!</font></div>";}else{        echo "<div>Found " . $totalNumber. " test cases.</div>";}$totalPage = ceil($totalNumber / $perNumber);if ($page < 1) {        $page = 1;}else if ($page > $totalPage){        $page = $totalPage;}$startCount = ($page - 1) * $perNumber;$sql = "select af.product, af.app, af.feature, testcases.t_id, testcases.title, testcases.description from testcases INNER JOIN af WHERE (" . $filter ." AND testcases.af_id = af.af_id) LIMIT $startCount, $perNumber ; ";$result = $mysqli->query($sql);//echo $sql."<br />";if ($result){        while ($row = mysqli_fetch_array($result)){?><tr><td><?php echo $row["product"]."-".$row["app"]."-".$row["feature"]."-".$row["t_id"];?></td><td><?php echo $row["title"];?></td><td><?php echo $row["description"];?></td><td><a href="javascript:" onclick = "show_delete_yes(this,<?php echo $row['t_id'];?>);" >Delete</a>&nbsp&nbsp<a href="edittc.php?id=<?php echo $row['t_id'];?>">Edit</a>&nbsp&nbsp<a href="result.php?id=<?php echo $row['t_id'];?>">Results</a></td></tr><?php		}}?></tbody></table><div><?phpif ($totalPage > 1){        if ($page != 1) {                echo "<a href='testcases.php?title=" . $_GET["title"]. "&description=" . $_GET["description"] . "&page=" . ($page - 1) . "'>Back</a>&nbsp";        }        for ( $i = 1; $i <= $totalPage; $i++){                if ($i == $page){                        echo "&nbsp" . $i . "&nbsp";                }                else{                        echo "&nbsp<a href='testcases.php?title=" . $_GET["title"]. "&description=" . $_GET["description"] . "&page=" . $i . "'>" . $i . "</a>&nbsp";                }        }        if ($page < $totalPage) {                echo "&nbsp<a href='testcases.php?title=" . $_GET["title"]. "&description=" . $_GET["description"] . "&page=" . ($page + 1) . "'>Next</a>";        }}?></div></div><script>function show_delete_yes(link, id){        var top = $(link).offset().top;        $("#deleteyes").css("display","block");        $("#deleteyes").css("top", top);        $("#t_id").val(id);}function hide_delete_yes(){        $("#deleteyes").css("display","none");}</script><div id = "deleteyes" style="display:none;"><div class = "confirm_delete_text">Are you sure to delete it?</div><form action = "deletetc.php" method = "POST"><input type = "hidden" name = "t_id" id = "t_id" /><input type = "submit" value = "Yes" /><input type = "button" value = "No" onclick = "hide_delete_yes();false;" /></form></div><?php}mysqli_close($mysqli);	include "footer.php";